import React, { useState, useEffect } from "react";

const TestCreator = () => {
  const [questions, setQuestions] = useState([]);
  const [selectedLevel, setSelectedLevel] = useState("");
  const [selectedSubject, setSelectedSubject] = useState("");

  useEffect(() => {
    // Fetch all questions from the server initially
    fetchProblems();
  }, [selectedSubject, selectedLevel]);

  const fetchProblems = async () => {
    try {
      const response = await fetch("http://localhost:3000/loadProblems", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          subject: selectedSubject,
          level: selectedLevel,
        }),
      });

      if (!response.ok) {
        throw new Error(`Failed to fetch problems. Status: ${response.status}`);
      }

      const data = await response.json();
      setQuestions(data);
    } catch (error) {
      console.error("Error fetching questions:", error.message);
    }
  };

  const handleLevelChange = (event) => {
    setSelectedLevel(event.target.value);
  };

  const handleSubjectChange = (event) => {
    setSelectedSubject(event.target.value);
  };

  return (
    <div>
      <h2>Questions List</h2>
      <div>
        <label htmlFor="levelDropdown">Level:</label>
        <select
          id="levelDropdown"
          value={selectedLevel}
          onChange={handleLevelChange}
        >
          <option value="">Select Level</option>
          <option value="easy">Easy</option>
          <option value="medium">Medium</option>
          <option value="hard">Hard</option>
        </select>
      </div>
      <div>
        <label htmlFor="subjectDropdown">Subject:</label>
        <select
          id="subjectDropdown"
          value={selectedSubject}
          onChange={handleSubjectChange}
        >
          <option value="">Select Subject</option>
          <option value="softskill">Soft Skill</option>
          <option value="technical">Technical</option>
          <option value="aptitude">Aptitude</option>
          <option value="verbal">Verbal</option>
        </select>
      </div>
      <button onClick={fetchProblems}>Fetch Problems</button>
      <ul>
        {questions.map((question) => (
          <li key={question._id}>
            <div>
              <strong>Subject:</strong> {question.subject}
            </div>
            <div>
              <strong>Level:</strong> {question.level}
            </div>
            <div>
              <strong>Question:</strong> {question.problemStatement}
            </div>
            <ul>
              <li>{question.option1}</li>
              <li>{question.option2}</li>
              <li>{question.option3}</li>
              <li>{question.option4}</li>
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TestCreator;
